import { useState } from "react";

function App() {
  const scanFile = (event) => {
    const file = event.target.files[0];

    const reader = new FileReader();
    reader.readAsText(file);
    reader.onload
  };

  return (
    <div>
      <input type="file" onChange={scanFile} />
      <button>Arrange Document</button>
    </div>
  );
}

export default App;
